import React from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Container,
  Typography,
  Stepper,
  Step,
  StepLabel,
  Button,
  Paper,
  TextField,
  Grid,
  FormControl,
  FormControlLabel,
  Radio,
  RadioGroup,
  MenuItem,
  Select,
  InputLabel,
  Alert,
} from '@mui/material';
import { useForm, Controller } from 'react-hook-form';

interface AdditionalInformationForm {
  industryType: 'grocery' | 'pharmacy' | 'electronics' | 'clothing' | 'restaurant' | 'other';
  purposeOfLoan: 'growth' | 'stock' | 'refinance';
  distributorPaymentRegularity: boolean;
  collateralProvided: boolean;
  collateralType?: 'jewel' | 'property';
  collateralValue?: number;
  loanAmountRequested: number;
}

// Store for persisting form data
const formDataStore = {
  additionalInformationDetails: {} as AdditionalInformationForm,
  setAdditionalInformationDetails: (data: AdditionalInformationForm) => {
    formDataStore.additionalInformationDetails = data;
  },
  getAdditionalInformationDetails: () => {
    return formDataStore.additionalInformationDetails;
  }
};

const steps = ['Financial Details', 'Credit History', 'Business Details', 'Operational Details', 'Additional Information'];

const AdditionalInformation = () => {
  const navigate = useNavigate();
  const { control, handleSubmit, formState: { errors }, watch } = useForm<AdditionalInformationForm>({
    defaultValues: {
      industryType: formDataStore.getAdditionalInformationDetails().industryType || 'grocery',
      purposeOfLoan: formDataStore.getAdditionalInformationDetails().purposeOfLoan || 'growth',
      distributorPaymentRegularity: formDataStore.getAdditionalInformationDetails().distributorPaymentRegularity ?? false,
      collateralProvided: formDataStore.getAdditionalInformationDetails().collateralProvided ?? false,
      collateralType: formDataStore.getAdditionalInformationDetails().collateralType,
      collateralValue: formDataStore.getAdditionalInformationDetails().collateralValue,
      loanAmountRequested: formDataStore.getAdditionalInformationDetails().loanAmountRequested || 0,
    },
  });

  const collateralProvided = watch('collateralProvided');

  const onSubmit = (data: AdditionalInformationForm) => {
    formDataStore.setAdditionalInformationDetails(data);
    navigate('/credit-report');
  };

  const getErrorMessage = (error: any): string => error?.message?.toString() || '';

  return (
    <Container maxWidth="md">
      <Box sx={{ py: 4 }}>
        <Typography variant="h4" align="center" gutterBottom>
          Additional Information
        </Typography>
        <Paper sx={{ p: 4, mt: 4 }}>
          <Stepper activeStep={4} sx={{ mb: 4 }}>
            {steps.map((label) => (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
              </Step>
            ))}
          </Stepper>

          <form onSubmit={handleSubmit(onSubmit)}>
            <Grid container spacing={3}>
              <Grid item xs={12}>
                <FormControl fullWidth required error={!!errors.industryType}>
                  <InputLabel>Industry Type</InputLabel>
                  <Controller
                    name="industryType"
                    control={control}
                    rules={{ required: 'Industry type is required' }}
                    render={({ field }) => (
                      <Select {...field} label="Industry Type">
                        <MenuItem value="grocery">Grocery/Kirana</MenuItem>
                        <MenuItem value="pharmacy">Pharmacy</MenuItem>
                        <MenuItem value="electronics">Electronics</MenuItem>
                        <MenuItem value="clothing">Clothing/Fashion</MenuItem>
                        <MenuItem value="restaurant">Restaurant</MenuItem>
                        <MenuItem value="other">Other</MenuItem>
                      </Select>
                    )}
                  />
                  {errors.industryType && (
                    <Typography color="error" variant="caption">
                      {getErrorMessage(errors.industryType)}
                    </Typography>
                  )}
                </FormControl>
              </Grid>

              <Grid item xs={12}>
                <FormControl component="fieldset" required error={!!errors.purposeOfLoan}>
                  <Typography variant="subtitle1" gutterBottom>
                    Purpose of Loan
                  </Typography>
                  <Controller
                    name="purposeOfLoan"
                    control={control}
                    rules={{ required: 'Purpose of loan is required' }}
                    render={({ field }) => (
                      <RadioGroup {...field} row>
                        <FormControlLabel value="growth" control={<Radio />} label="Growth/Expansion" />
                        <FormControlLabel value="stock" control={<Radio />} label="Stock Purchase" />
                        <FormControlLabel value="refinance" control={<Radio />} label="Debt Refinance" />
                      </RadioGroup>
                    )}
                  />
                  {errors.purposeOfLoan && (
                    <Typography color="error" variant="caption">
                      {getErrorMessage(errors.purposeOfLoan)}
                    </Typography>
                  )}
                </FormControl>
              </Grid>

              <Grid item xs={12}>
                <FormControl component="fieldset" required error={!!errors.distributorPaymentRegularity}>
                  <Typography variant="subtitle1" gutterBottom>
                    Distributor Payment Regularity
                  </Typography>
                  <Controller
                    name="distributorPaymentRegularity"
                    control={control}
                    rules={{ required: 'Distributor payment regularity is required' }}
                    render={({ field }) => (
                      <RadioGroup {...field} value={field.value ? 'yes' : 'no'}>
                        <FormControlLabel value="yes" control={<Radio />} label="Yes" />
                        <FormControlLabel value="no" control={<Radio />} label="No" />
                      </RadioGroup>
                    )}
                  />
                  {errors.distributorPaymentRegularity && (
                    <Typography color="error" variant="caption">
                      {getErrorMessage(errors.distributorPaymentRegularity)}
                    </Typography>
                  )}
                </FormControl>
              </Grid>

              <Grid item xs={12}>
                <FormControl component="fieldset" required error={!!errors.collateralProvided}>
                  <Typography variant="subtitle1" gutterBottom>
                    Collateral Provided
                  </Typography>
                  <Controller
                    name="collateralProvided"
                    control={control}
                    rules={{ required: 'Collateral information is required' }}
                    render={({ field }) => (
                      <RadioGroup {...field} value={field.value ? 'yes' : 'no'}>
                        <FormControlLabel value="yes" control={<Radio />} label="Yes" />
                        <FormControlLabel value="no" control={<Radio />} label="No" />
                      </RadioGroup>
                    )}
                  />
                  {errors.collateralProvided && (
                    <Typography color="error" variant="caption">
                      {getErrorMessage(errors.collateralProvided)}
                    </Typography>
                  )}
                </FormControl>

                {collateralProvided && (
                  <Box sx={{ pl: 4, mt: 2 }}>
                    <Alert severity="info" sx={{ mb: 2 }}>
                      Note: Only Jewel and Property are accepted as collateral.
                    </Alert>
                    <FormControl component="fieldset" fullWidth required error={!!errors.collateralType}>
                      <Typography variant="subtitle1" gutterBottom>
                        Type of Collateral
                      </Typography>
                      <Controller
                        name="collateralType"
                        control={control}
                        rules={{ required: 'Collateral type is required' }}
                        render={({ field }) => (
                          <RadioGroup {...field}>
                            <FormControlLabel value="jewel" control={<Radio />} label="Jewel" />
                            <FormControlLabel value="property" control={<Radio />} label="Property" />
                          </RadioGroup>
                        )}
                      />
                      {errors.collateralType && (
                        <Typography color="error" variant="caption">
                          {getErrorMessage(errors.collateralType)}
                        </Typography>
                      )}
                    </FormControl>
                    <Box sx={{ mt: 2 }}>
                      <Controller
                        name="collateralValue"
                        control={control}
                        rules={{ required: 'Market value of collateral is required' }}
                        render={({ field }) => (
                          <TextField
                            {...field}
                            label="Market Value of the Collateral (₹)"
                            type="number"
                            fullWidth
                            required
                            error={!!errors.collateralValue}
                            helperText={getErrorMessage(errors.collateralValue)}
                          />
                        )}
                      />
                    </Box>
                  </Box>
                )}
              </Grid>

              <Grid item xs={12}>
                <Controller
                  name="loanAmountRequested"
                  control={control}
                  rules={{ required: 'Loan amount is required' }}
                  render={({ field }) => (
                    <TextField
                      {...field}
                      label="Loan Amount Requested (₹)"
                      type="number"
                      fullWidth
                      required
                      error={!!errors.loanAmountRequested}
                      helperText={getErrorMessage(errors.loanAmountRequested)}
                    />
                  )}
                />
              </Grid>

              <Grid item xs={12} sx={{ mt: 3, display: 'flex', justifyContent: 'space-between' }}>
                <Button
                  variant="outlined"
                  onClick={() => navigate('/operational-efficiency')}
                >
                  Back
                </Button>
                <Button
                  type="submit"
                  variant="contained"
                  color="primary"
                >
                  Next
                </Button>
              </Grid>
            </Grid>
          </form>
        </Paper>
      </Box>
    </Container>
  );
};

export default AdditionalInformation;
