import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Container,
  Typography,
  Stepper,
  Step,
  StepLabel,
  Button,
  Paper,
  TextField,
  FormControl,
  FormControlLabel,
  Radio,
  RadioGroup,
  MenuItem,
  Grid,
  Slider,
  Alert,
} from '@mui/material';
import { useForm, Controller, FieldValues } from 'react-hook-form';

const steps = ['Financial Details', 'Business Details', 'Credit History', 'Operational Details', 'Additional Information'];

interface FinancialDetails {
  storeName: string;
  monthlySales: number;
  profitMargin: number;
  monthlyEMI: number;
  averageBankBalance: number;
  buildingOwnership: 'own' | 'rent';
  collateralProvided: boolean;
  collateralType?: 'jewel' | 'property';
  collateralValue?: number;
  itrFiled: boolean;
}

// Create a store to persist form data across pages
const formDataStore = {
  financialDetails: {} as FinancialDetails,
  setFinancialDetails: (data: FinancialDetails) => {
    formDataStore.financialDetails = data;
  },
  getFinancialDetails: () => {
    return formDataStore.financialDetails;
  }
};

const StoreDetails = () => {
  const [activeStep, setActiveStep] = useState(0);
  const navigate = useNavigate();
  const { control, handleSubmit, formState: { errors }, watch, setValue } = useForm<FinancialDetails>({
    defaultValues: {
      ...formDataStore.getFinancialDetails(),
      collateralProvided: formDataStore.getFinancialDetails().collateralProvided ?? false,
      itrFiled: formDataStore.getFinancialDetails().itrFiled ?? false
    }
  });

  // Watch the collateralProvided value to conditionally show fields
  const collateralProvided = watch('collateralProvided');

  // Reset collateral details when "No" is selected
  React.useEffect(() => {
    if (!collateralProvided) {
      setValue('collateralType', undefined);
      setValue('collateralValue', undefined);
    }
  }, [collateralProvided, setValue]);

  const handleNext = () => {
    setActiveStep((prevStep) => prevStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevStep) => prevStep - 1);
  };

  const onSubmit = async (data: FinancialDetails) => {
    try {
      console.log('Form submitted with data:', data);
      // Store the form data before moving to next step
      formDataStore.setFinancialDetails(data);
      console.log('Stored form data:', data);

      // Navigate to the credit history page
      console.log('Navigating to credit history page...');
      navigate('/credit-history');
    } catch (error) {
      console.error('Error during form submission:', error);
    }
  };

  const getErrorMessage = (error: any): string => {
    return error?.message?.toString() || '';
  };

  const renderStepContent = (step: number) => {
    switch (step) {
      case 0:
        return (
          <form 
            id="financialDetailsForm" 
            noValidate
            onSubmit={async (e) => {
              e.preventDefault();
              console.log('Form submit event triggered');
              const result = await handleSubmit(onSubmit)(e);
              console.log('Form submission result:', result);
            }}
          >
            <Grid container spacing={3}>
              <Grid item xs={12}>
                <Controller
                  name="storeName"
                  control={control}
                  defaultValue={formDataStore.getFinancialDetails().storeName || ''}
                  rules={{ required: 'Store name is required' }}
                  render={({ field }) => (
                    <TextField
                      {...field}
                      label="Store Name"
                      fullWidth
                      required
                      error={!!errors.storeName}
                      helperText={getErrorMessage(errors.storeName)}
                    />
                  )}
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <Controller
                  name="monthlySales"
                  control={control}
                  defaultValue={formDataStore.getFinancialDetails().monthlySales || 0}
                  rules={{ required: 'Monthly sales is required' }}
                  render={({ field }) => (
                    <TextField
                      {...field}
                      label="Monthly Sales (₹)"
                      type="number"
                      fullWidth
                      required
                      error={!!errors.monthlySales}
                      helperText={getErrorMessage(errors.monthlySales)}
                    />
                  )}
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <Controller
                  name="profitMargin"
                  control={control}
                  defaultValue={formDataStore.getFinancialDetails().profitMargin || 0}
                  rules={{ required: 'Profit margin is required' }}
                  render={({ field }) => (
                    <Box>
                      <Typography gutterBottom>
                        Profit Margin: {field.value}%
                      </Typography>
                      <Slider
                        {...field}
                        valueLabelDisplay="auto"
                        step={1}
                        marks
                        min={0}
                        max={100}
                        valueLabelFormat={(value) => `${value}%`}
                        sx={{ mt: 2 }}
                      />
                    </Box>
                  )}
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <Controller
                  name="monthlyEMI"
                  control={control}
                  defaultValue={formDataStore.getFinancialDetails().monthlyEMI || 0}
                  rules={{ required: 'Monthly EMI is required' }}
                  render={({ field }) => (
                    <TextField
                      {...field}
                      label="Monthly EMI (₹)"
                      type="number"
                      fullWidth
                      required
                      error={!!errors.monthlyEMI}
                      helperText={getErrorMessage(errors.monthlyEMI)}
                    />
                  )}
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <Controller
                  name="averageBankBalance"
                  control={control}
                  defaultValue={formDataStore.getFinancialDetails().averageBankBalance || 0}
                  rules={{ required: 'Average bank balance is required' }}
                  render={({ field }) => (
                    <TextField
                      {...field}
                      label="Average Bank Balance (₹)"
                      type="number"
                      fullWidth
                      required
                      error={!!errors.averageBankBalance}
                      helperText={getErrorMessage(errors.averageBankBalance)}
                    />
                  )}
                />
              </Grid>
              <Grid item xs={12}>
                <FormControl component="fieldset" required error={!!errors.buildingOwnership}>
                  <Typography variant="subtitle1" gutterBottom>
                    Building Ownership
                  </Typography>
                  <Controller
                    name="buildingOwnership"
                    control={control}
                    defaultValue={formDataStore.getFinancialDetails().buildingOwnership || 'rent'}
                    rules={{ required: 'Building ownership is required' }}
                    render={({ field }) => (
                      <RadioGroup {...field}>
                        <FormControlLabel value="own" control={<Radio />} label="Own" />
                        <FormControlLabel value="rent" control={<Radio />} label="Rent" />
                      </RadioGroup>
                    )}
                  />
                  {errors.buildingOwnership && (
                    <Typography color="error" variant="caption">
                      {getErrorMessage(errors.buildingOwnership)}
                    </Typography>
                  )}
                </FormControl>
              </Grid>
              <Grid item xs={12}>
                <FormControl component="fieldset" required error={!!errors.collateralProvided}>
                  <Typography variant="subtitle1" gutterBottom>
                    Collateral Provided
                  </Typography>
                  <Controller
                    name="collateralProvided"
                    control={control}
                    defaultValue={false}
                    rules={{ required: 'Collateral information is required' }}
                    render={({ field: { onChange, value, ...field } }) => (
                      <RadioGroup
                        {...field}
                        value={value ? 'yes' : 'no'}
                        onChange={(e) => {
                          const newValue = e.target.value === 'yes';
                          onChange(newValue);
                          if (!newValue) {
                            setValue('collateralType', undefined);
                            setValue('collateralValue', undefined);
                          }
                        }}
                      >
                        <FormControlLabel value="yes" control={<Radio />} label="Yes" />
                        <FormControlLabel value="no" control={<Radio />} label="No" />
                      </RadioGroup>
                    )}
                  />
                  {errors.collateralProvided && (
                    <Typography color="error" variant="caption">
                      {getErrorMessage(errors.collateralProvided)}
                    </Typography>
                  )}
                </FormControl>

                {collateralProvided && (
                  <Box sx={{ pl: 4, mt: 2 }}>
                    <Alert severity="info" sx={{ mb: 2 }}>
                      Note: Only Jewel and Property are accepted as collateral.
                    </Alert>
                    <FormControl component="fieldset" fullWidth required error={!!errors.collateralType}>
                      <Typography variant="subtitle1" gutterBottom>
                        Type of Collateral
                      </Typography>
                      <Controller
                        name="collateralType"
                        control={control}
                        defaultValue={formDataStore.getFinancialDetails().collateralType || 'jewel'}
                        rules={{ required: 'Collateral type is required' }}
                        render={({ field }) => (
                          <RadioGroup {...field}>
                            <FormControlLabel value="jewel" control={<Radio />} label="Jewel" />
                            <FormControlLabel value="property" control={<Radio />} label="Property" />
                          </RadioGroup>
                        )}
                      />
                      {errors.collateralType && (
                        <Typography color="error" variant="caption">
                          {getErrorMessage(errors.collateralType)}
                        </Typography>
                      )}
                    </FormControl>
                    <Box sx={{ mt: 2 }}>
                      <Controller
                        name="collateralValue"
                        control={control}
                        defaultValue={formDataStore.getFinancialDetails().collateralValue || 0}
                        rules={{ required: 'Market value of collateral is required' }}
                        render={({ field }) => (
                          <TextField
                            {...field}
                            label="Market Value of the Collateral (₹)"
                            type="number"
                            fullWidth
                            required
                            error={!!errors.collateralValue}
                            helperText={getErrorMessage(errors.collateralValue)}
                          />
                        )}
                      />
                    </Box>
                  </Box>
                )}
              </Grid>
              <Grid item xs={12}>
                <FormControl component="fieldset" required error={!!errors.itrFiled}>
                  <Typography variant="subtitle1" gutterBottom>
                    ITR Filed Regularly
                  </Typography>
                  <Controller
                    name="itrFiled"
                    control={control}
                    defaultValue={false}
                    rules={{ required: 'ITR filing information is required' }}
                    render={({ field: { onChange, value, ...field } }) => (
                      <RadioGroup
                        {...field}
                        value={value ? 'yes' : 'no'}
                        onChange={(e) => onChange(e.target.value === 'yes')}
                      >
                        <FormControlLabel value="yes" control={<Radio />} label="Yes" />
                        <FormControlLabel value="no" control={<Radio />} label="No" />
                      </RadioGroup>
                    )}
                  />
                  {errors.itrFiled && (
                    <Typography color="error" variant="caption">
                      {getErrorMessage(errors.itrFiled)}
                    </Typography>
                  )}
                </FormControl>
              </Grid>
            </Grid>
          </form>
        );
      default:
        return null;
    }
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ py: 4 }}>
        <Typography variant="h4" align="center" gutterBottom>
          Store Details
        </Typography>
        <Paper sx={{ p: 4, mt: 4 }}>
          <Stepper activeStep={activeStep} sx={{ mb: 4 }}>
            {steps.map((label) => (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
              </Step>
            ))}
          </Stepper>

          {renderStepContent(activeStep)}

          <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 4 }}>
            <Button
              disabled={activeStep === 0}
              onClick={handleBack}
              variant="outlined"
            >
              Back
            </Button>
            {activeStep === steps.length - 1 ? (
              <Button
                variant="contained"
                color="primary"
                onClick={handleSubmit(onSubmit)}
              >
                Calculate Score
              </Button>
            ) : (
              <Button
                type="submit"
                form="financialDetailsForm"
                variant="contained"
                color="primary"
              >
                Next
              </Button>
            )}
          </Box>
        </Paper>
      </Box>
    </Container>
  );
};

export default StoreDetails;
