import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Container,
  Typography,
  Stepper,
  Step,
  StepLabel,
  Button,
  Paper,
  TextField,
  FormControl,
  FormControlLabel,
  Radio,
  RadioGroup,
  MenuItem,
  Grid,
} from '@mui/material';
import { useForm, Controller } from 'react-hook-form';

const steps = ['Basic Information', 'Financial Details', 'Credit History', 'Operational Details'];

const StoreDetails = () => {
  const [activeStep, setActiveStep] = useState(0);
  const navigate = useNavigate();
  const { control, handleSubmit, formState: { errors } } = useForm();

  const handleNext = () => {
    setActiveStep((prevStep) => prevStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevStep) => prevStep - 1);
  };

  const onSubmit = (data: any) => {
    console.log(data);
    // Here we'll implement the credit score calculation
    navigate('/credit-report');
  };

  const renderStepContent = (step: number) => {
    switch (step) {
      case 0:
        return (
          <Grid container spacing={3}>
            <Grid item xs={12}>
              <Controller
                name="storeName"
                control={control}
                defaultValue=""
                rules={{ required: 'Store name is required' }}
                render={({ field }) => (
                  <TextField
                    {...field}
                    label="Store Name"
                    fullWidth
                    error={!!errors.storeName}
                    helperText={errors.storeName?.message}
                  />
                )}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <Controller
                name="yearsInOperation"
                control={control}
                defaultValue=""
                rules={{ required: 'Years in operation is required' }}
                render={({ field }) => (
                  <TextField
                    {...field}
                    label="Years in Operation"
                    type="number"
                    fullWidth
                    error={!!errors.yearsInOperation}
                    helperText={errors.yearsInOperation?.message}
                  />
                )}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <Controller
                name="shopSize"
                control={control}
                defaultValue=""
                rules={{ required: 'Shop size is required' }}
                render={({ field }) => (
                  <TextField
                    {...field}
                    label="Shop Size (sq.ft.)"
                    type="number"
                    fullWidth
                    error={!!errors.shopSize}
                    helperText={errors.shopSize?.message}
                  />
                )}
              />
            </Grid>
          </Grid>
        );

      case 1:
        return (
          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <Controller
                name="monthlySales"
                control={control}
                defaultValue=""
                rules={{ required: 'Monthly sales is required' }}
                render={({ field }) => (
                  <TextField
                    {...field}
                    label="Monthly Sales"
                    type="number"
                    fullWidth
                    error={!!errors.monthlySales}
                    helperText={errors.monthlySales?.message}
                  />
                )}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <Controller
                name="profitMargin"
                control={control}
                defaultValue=""
                rules={{ required: 'Profit margin is required' }}
                render={({ field }) => (
                  <TextField
                    {...field}
                    label="Profit Margin (%)"
                    type="number"
                    fullWidth
                    error={!!errors.profitMargin}
                    helperText={errors.profitMargin?.message}
                  />
                )}
              />
            </Grid>
            <Grid item xs={12}>
              <FormControl component="fieldset">
                <Typography variant="subtitle1" gutterBottom>
                  Building Ownership
                </Typography>
                <Controller
                  name="buildingOwnership"
                  control={control}
                  defaultValue=""
                  rules={{ required: 'Building ownership is required' }}
                  render={({ field }) => (
                    <RadioGroup {...field}>
                      <FormControlLabel value="own" control={<Radio />} label="Own" />
                      <FormControlLabel value="rent" control={<Radio />} label="Rent" />
                    </RadioGroup>
                  )}
                />
              </FormControl>
            </Grid>
          </Grid>
        );

      case 2:
        return (
          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <Controller
                name="cibilScore"
                control={control}
                defaultValue=""
                render={({ field }) => (
                  <TextField
                    {...field}
                    label="CIBIL Score (Optional)"
                    type="number"
                    fullWidth
                  />
                )}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <Controller
                name="bankingYears"
                control={control}
                defaultValue=""
                rules={{ required: 'Banking relationship years is required' }}
                render={({ field }) => (
                  <TextField
                    {...field}
                    label="Banking Relationship (Years)"
                    type="number"
                    fullWidth
                    error={!!errors.bankingYears}
                    helperText={errors.bankingYears?.message}
                  />
                )}
              />
            </Grid>
            <Grid item xs={12}>
              <Controller
                name="defaults"
                control={control}
                defaultValue="0"
                render={({ field }) => (
                  <TextField
                    {...field}
                    label="Number of Defaults"
                    type="number"
                    fullWidth
                  />
                )}
              />
            </Grid>
          </Grid>
        );

      case 3:
        return (
          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <Controller
                name="digitalPayments"
                control={control}
                defaultValue=""
                rules={{ required: 'Digital payments percentage is required' }}
                render={({ field }) => (
                  <TextField
                    {...field}
                    label="Digital Payments Adoption (%)"
                    type="number"
                    fullWidth
                    error={!!errors.digitalPayments}
                    helperText={errors.digitalPayments?.message}
                  />
                )}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <Controller
                name="inventoryTurnover"
                control={control}
                defaultValue=""
                rules={{ required: 'Inventory turnover is required' }}
                render={({ field }) => (
                  <TextField
                    {...field}
                    select
                    label="Inventory Turnover"
                    fullWidth
                    error={!!errors.inventoryTurnover}
                    helperText={errors.inventoryTurnover?.message}
                  >
                    <MenuItem value="weekly">Weekly</MenuItem>
                    <MenuItem value="monthly">Monthly</MenuItem>
                    <MenuItem value="quarterly">Quarterly</MenuItem>
                    <MenuItem value="slower">Slower</MenuItem>
                  </TextField>
                )}
              />
            </Grid>
            <Grid item xs={12}>
              <Controller
                name="onlinePresence"
                control={control}
                defaultValue=""
                rules={{ required: 'Online presence is required' }}
                render={({ field }) => (
                  <TextField
                    {...field}
                    select
                    label="Online Presence"
                    fullWidth
                    error={!!errors.onlinePresence}
                    helperText={errors.onlinePresence?.message}
                  >
                    <MenuItem value="none">None</MenuItem>
                    <MenuItem value="social">Social Media Only</MenuItem>
                    <MenuItem value="website">Website</MenuItem>
                    <MenuItem value="ecommerce">E-commerce Listing</MenuItem>
                  </TextField>
                )}
              />
            </Grid>
          </Grid>
        );

      default:
        return null;
    }
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ py: 4 }}>
        <Typography variant="h4" align="center" gutterBottom>
          Store Details
        </Typography>
        <Paper sx={{ p: 4, mt: 4 }}>
          <Stepper activeStep={activeStep} sx={{ mb: 4 }}>
            {steps.map((label) => (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
              </Step>
            ))}
          </Stepper>

          <form onSubmit={handleSubmit(onSubmit)}>
            {renderStepContent(activeStep)}

            <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 4 }}>
              <Button
                disabled={activeStep === 0}
                onClick={handleBack}
                variant="outlined"
              >
                Back
              </Button>
              {activeStep === steps.length - 1 ? (
                <Button
                  type="submit"
                  variant="contained"
                  color="primary"
                >
                  Calculate Score
                </Button>
              ) : (
                <Button
                  variant="contained"
                  color="primary"
                  onClick={handleNext}
                >
                  Next
                </Button>
              )}
            </Box>
          </form>
        </Paper>
      </Box>
    </Container>
  );
};

export default StoreDetails;
